# -----------------------------
# STEP 1: Factor n (core attack)
# -----------------------------
R = (c1 - c2) * inverse(c2 - c3, n) % n
p = math.gcd(R - 1, n)
q = n // p

print("[+] p found")
print("[+] q found")

# -----------------------------
# STEP 2: Build ciphertext matrix
# -----------------------------
C = [
    [c0, (-c1) % n, (-c2) % n, (-c3) % n],
    [c1, c0, (-c3) % n, c2 % n],
    [c2, c3, c0, (-c1) % n],
    [c3, (-c2) % n, c1 % n, c0],
]


# -----------------------------
# Matrix functions
# -----------------------------
def matrix_mult(A, B):
    M = [[0] * 4 for _ in range(4)]
    for i in range(4):
        for j in range(4):
            for k in range(4):
                M[i][j] = (M[i][j] + A[i][k] * B[k][j]) % n
    return M


def matrix_pow(A, exp):
    R = [[1 if i == j else 0 for j in range(4)] for i in range(4)]
    while exp > 0:
        if exp & 1:
            R = matrix_mult(R, A)
        A = matrix_mult(A, A)
        exp >>= 1
    return R


# -----------------------------
# STEP 3: Compute private exponent
# -----------------------------
phi = (p * p - 1) * (q * q - 1) // math.gcd(p * p - 1, q * q - 1)
d = inverse(e, phi)

# -----------------------------
# STEP 4: Decrypt
# -----------------------------
A = matrix_pow(C, d)
m = A[0][0]

print("[+] Flag:", long_to_bytes(m))
